import {useState,useEffect} from "react";


function Footer({checkedState,missions,setMissions}){

  const [count,setCount] = useState(0);

  useEffect(() => {
    let fCount=0;
    checkedState.forEach((state) => state ? fCount=fCount : fCount+=1 )
    setCount(fCount);
  },[checkedState])


   return(
     <div>
     <footer className="footer">
       <span className="todo-count">
         <strong>{count} {" "}</strong>
         items left
       </span>

       <ul className="filters">
         <li>
           <a className="selected" id="All">All</a>
         </li>
         <li>
           <a id="active">Active</a>
         </li>
         <li>
           <a id="completed">Completed</a>
         </li>
       </ul>

       <button className="clear-completed">
         Clear completed
       </button>
     </footer>

     </div>
   )


}

export default Footer;
