import {useState} from "react";
import Footer from "./Footer";

function List({missions,checkedState,setCheckedState}){

  const [count,setCount] = useState(0);

  const checkHandler = (id) => {
    const updatedCheckedState=checkedState.map((state,index) => {
      if(index === id) {
        state= !state;
        setCount(count - 1);
      }else{
        state = state;
        setCount(count + 1);
      }
    } );
    setCheckedState(updatedCheckedState);
  }

  return (
    <div>
    <section className="main">
   		<input className="toggle-all" type="checkbox" />
   		<label for="toggle-all">
   			Mark all as complete
   		</label>

   		<ul className="todo-list">
       {
         missions.map((mission,index) => (

           <li key={index} className = {checkedState[index] ? "completed" : null}>
      				<div className="view">
      					<input className="toggle" type="checkbox"  onChange={()=> checkHandler(index)} value={mission[index]} id={index}/>
      					<label >{mission}</label>
      					<button className="destroy"></button>
      				</div>
      			</li>
         ))
       }

   		</ul>
   	</section>
    <Footer
    count={count}
    />
    </div>
  )
}

export default List;
