import {useState,useEffect} from "react";


function Footer({checkedState,setCheckedState,
  missions,setMissions,setHide}){

  const [count,setCount] = useState(0);

  useEffect(() => {
    let fCount=0;
    checkedState.forEach((state) => state ? fCount=fCount : fCount+=1 )
    setCount(fCount);
  },[checkedState])

  const clearCompleted = () => {
    setMissions(missions.filter((mission,index) => checkedState[index] === false))
    setCheckedState(checkedState.filter((state) => state === false));
  }

  const selectedButton = (e) => {
    switch(e.target.id){
      case "All":
        setHide("All");
        break;
      case "Active":
        setHide("Active");
        break;
      case "Completed":
        setHide("Completed");
        break;
      default:
    }
  }

   return(
     <div>
     <footer className="footer">
       <span className="todo-count">
         <strong>{count} {" "}</strong>
         items left
       </span>

       <ul className="filters">
         <li>
           <a href="#/" className="selected" id="All" onClick={selectedButton}>All</a>
         </li>
         <li>
           <a href="#/" id="Active" onClick={selectedButton}>Active</a>
         </li>
         <li>
           <a href ="#/" id="Completed" onClick={selectedButton}>Completed</a>
         </li>
       </ul>

       <button className="clear-completed" onClick={clearCompleted}>
         Clear completed
       </button>
     </footer>

     </div>
   )


}

export default Footer;
