function List({missions,setMissions,checkedState,setCheckedState}){

  const checkHandler = (id) => {
    const updatedCheckedState=checkedState.map((state,index) => index === id ? !state : state );
    setCheckedState(updatedCheckedState);
  }

  function submitDelete(event,id){
    event.preventDefault();
    event.stopPropagation();
    setMissions(prevTasks => {
      return prevTasks.filter((item,index) => {
        return index !== id
      });
    });
    setCheckedState(prevChecks => {

      return prevChecks.filter((item,index) => {
        return index !==id
      })
    });
  }

  return (
    <section className="main">
   		<input className="toggle-all" type="checkbox" />
   		<label for="toggle-all">
   			Mark all as complete
   		</label>

   		<ul className="todo-list">
       {
         missions.map((mission,index) => (

           <li key={index} className = {checkedState[index] ? "completed" : null}>
      				<div className="view">
      					<input className="toggle" type="checkbox"  onChange={()=> checkHandler(index)} value={mission[index]} id={index} checked={checkedState[index]}/>
      					<label >{mission}</label>
      					<button className="destroy" onClick={(event) => submitDelete(event,index)}></button>
      				</div>
      			</li>
         ))
       }

   		</ul>
   	</section>
  )
}

export default List;
