import {useState,useEffect} from "react";


function Footer({checkedState,missions,setMissions}){

  const [count,setCount] = useState(0);

  useEffect(() => {
    let fCount=0;
    checkedState.forEach((state) => state ? fCount=fCount : fCount+=1 )
    setCount(fCount);
  },[checkedState])

  const clearCompleted = () => (
    setMissions(missions.filter((mission) => mission.checked === false))
  );

   return(
     <div>
     <footer className="footer">
       <span className="todo-count">
         <strong>{count} {" "}</strong>
         items left
       </span>

       <ul className="filters">
         <li>
           <a href="#/" role="button" className="selected">All</a>
         </li>
         <li>
           <a href="#active" role="button">Active</a>
         </li>
         <li>
           <a href="#completed" role="button">Completed</a>
         </li>
       </ul>

       <button className="clear-completed" onClick={clearCompleted}>
         Clear completed
       </button>
     </footer>

     </div>
   )


}

export default Footer;
