import {useState} from "react";


function Header({missions,setMissions}){

  const [input,setInput] = useState("");

  const handleChange=(event) => {
    const newInput = event.target.value;
    setInput(newInput);
  }

 const handleAdd= (event) => {
   event.preventDefault();
   setMissions([...missions,input]);
 }

 missions.forEach(mission =>console.log(mission))

   return(
     <section className="todoapp">
     <header className="header">
        <h1>todos</h1>
        <form onSubmit={handleAdd}>
          <input onChange={handleChange} className="new-todo" placeholder="What needs to be done?" autoFocus value={input}/>
        </form>
      </header>
     </section>
   )


}

export default Header;
